package core_java_14_may_2022;

public class Variable_String {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//just print the string if we use "" it will print only inside colen value
		
		
		int x =32;
		
		System.out.println(x);//ans:32
		
		//in this we have two x but its working just because int x and only x 
		
		x = 78;
		
		System.out.println(x + 4);//ans:82
		
		String words;
		
		words = "This is a sentence";
		
		System.out.println(words + "some extra words");//ans:This is a sentence some extra words
		
		x=300;
		
		System.out.println(x);//300

	}

}
