package core_java_hw;

public class String_class {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// String class method 
		
		String str = "Sachin";
		
		//to upper case method 
		
		System.out.println(str.toUpperCase()); //ans:SACHIN
		
		//to lower case method
		
		System.out.println(str.toLowerCase());//ans:sachin
		
		
		System.out.println(str);//ans:Sachin
		
		
		
		
		
		

	}

}
